import React, {useState, useContext} from 'react'
import { useTranslation } from 'react-i18next'
import { TasksContext } from '../context'

import "./FormTask.css"

const FormTask = () => {
    const {t} = useTranslation()
    const [form, setForm] = useState({title: "", desc: ""})    
    const {addTask} = useContext(TasksContext)

    function handleChange(e) {
        const {name, value} = e.target
        
        setForm(prevState => (
            {
                ...prevState,
                [name]: value
            }
        ))
    }    

    function handleSubmit(e) {
        e.preventDefault()

        if(form.title === ""){

            
            return false
        }

        if(form.desc === ""){

            

            return false
        }

        addTask(form)
        setForm({title: "", desc: ""})        

    }

    return (                
        <form onSubmit={handleSubmit}>
            <div className="form-group">     
                <label htmlFor='title'>
                    {t("form-task.input1")}
                </label>           
                <input 
                    id='title' 
                    name='title' 
                    value={form.title} 
                    onChange={handleChange} />
                <div class='errorInput hidden' id='errorInput1'>{t("form-task.input1-error")}</div>    
            </div>
            <div className="form-group">    
                <label htmlFor='desc'>
                    {t("form-task.input2")}
                </label>                  
                <textarea 
                    id='desc' 
                    name='desc' 
                    value={form.desc} 
                    onChange={handleChange} />
                <div class='errorInput' id='errorInput2'>{t("form-task.input2-error")}</div>    
            </div>
            <button type="submit">{t("form-task.button")}</button>
        </form>        
    )
}

export default FormTask
