import React, {useContext} from 'react'
import ReactDOM from "react-dom"

import { TasksContext } from '../context'

import "./Modal.css"

const Modal = () => {   

    const {tasks, modal, setModal, removeTask} = useContext(TasksContext)   
    let modalBody;
    
    if(!modal.opened) return null

    function handleClick (e) {
        switch(e.target.name){
            case "yesDel":
                removeTask(modal.taskid)
                break;
            case "noDel":
                setModal({opened: false, taskid: 0, type: 'none'})
                break;
            case "edit":  
                const task = tasks.find(task => task.id === modal.taskid)    
                console.log(task)  
                //const [editForm, setEditForm] = useContext({title: task.title, description: task.description})       
                break;
            default:
                setModal({opened: false, taskid: 0, type: 'none'})
                break;
        }
    }

    switch(modal.type){
        case 'DELETE':
            modalBody = <>           
                            <p>Â¿Realmente desea borrar la tarea?</p>           
                            <div>
                                <button className='modalBtn' name="yesDel" onClick={handleClick}>Si</button>
                                <button className='modalBtn' name="noDel"  onClick={handleClick}>No</button>
                            </div>                         
                        </>            
            break
        case 'EDIT':
           
            break
        default:
            break
    }   
    
    return ReactDOM.createPortal(      
        <>
            <div className='overlay' name="overlay" onClick={handleClick} />
            <div className='modal'>            
                {modalBody}
            </div>
        </>,
       document.getElementById("portal")
   )
}

export default Modal
